mapper.select:
select a.DN_ID ID,a.DN_CHN CN,a.DN_ENG EN,b.DN_CHN STAD_CN,b.DN_ENG STAD_EN,a.TERM_TYPE,
	   a.SYNO_TYPE,a.ORG_DN_CHN ORG_CN,a.ORG_DN_ENG ORG_EN,a.IS_DISABLE,a.DESCRIPTION,a.UPDATE_MAN,a.UPDATE_TIME
from on_diag_name a 
left join on_diag_name b on (a.STAD_DN_ID=b.DN_ID and b.STAD_DN_ID=b.DN_ID 
	
		and b.STAD_DN_ID = #{pd.standardId} 
	
	) 
where 1=1 

	and	a.STAD_DN_ID != a.DN_ID 


	and	a.STAD_DN_ID = a.DN_ID 


	and a.STAD_DN_ID = #{pd.standardId} 

 
	and a.DN_CHN = #{pd.DN_CHN} 

 
	and a.DN_CHN like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') 

 
	and to_char(a.UPDATE_TIME,'YYYY-MM-DD') >= #{pd.UPDATE_TIME} 

 
	and to_char(a.UPDATE_TIME,'YYYY-MM-DD') <= #{pd.UPDATE_TIME_END}

select m.dn_chn stand_name,n.dn_id,n.dn_chn,n.dn_eng,n.stad_dn_id,n.term_type,n.syno_type,n.org_dn_chn,n.org_dn_eng,
 n.is_disable,n.update_man,n.update_time,decode(Y.DN_ID,null,0,'',0,1) OSYN_EDIT_FLAG from on_diag_name n left join on_diag_name m on m.dn_id=n.stad_dn_id 
 left join (select distinct DN_ID from ai_alter_name_hist where ONTO_TYPE=#{pd.TERM_CATEGORY} and STATUS=0 ) y on n.dn_id =y.dn_id
 where 1=1
 
	 and n.dn_id!=n.stad_dn_id
 
 
	 and n.dn_id = n.stad_dn_id
 
 
 	and n.term_type=#{pd.TERM_TYPE}
 
 
 	and n.syno_type=#{pd.OSYN_TYPE}
 
 
 	and n.is_disable = #{pd.IS_DISABLED}
 
 
 	and n.update_man = #{pd.UPDATE_MAN}
 
 
 	and to_char(n.UPDATE_TIME,'YYYY-MM-DD') >= #{pd.UPDATE_TIME}
 
 
 	and to_char(n.UPDATE_TIME,'YYYY-MM-DD') <= #{pd.UPDATE_TIME_END}
 
 
 	and (n.dn_chn like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%')
 		or n.dn_eng like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%')
 		or m.dn_chn like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%')
 	)
 
 order by n.dn_id  

	order by t.update_time desc


	order by t.update_time asc

SELECT s.on_chn standard_name,P.ON_ID,P.ON_CHN,P.ON_ENG,P.STAD_ON_ID,P.SYNO_TYPE,P.ORG_OP_CHN,P.ORG_OP_ENG,
P.IS_DISABLE,P.VERSION,P.UPDATE_MAN,P.UPDATE_TIME,P.DESCRIPTION,P.RECORD,P.COLUMN_14,
decode(Y.DN_ID,null,0,'',0,1) OSYN_EDIT_FLAG FROM ON_OPERATION_NAME p left join ON_OPERATION_NAME s on 
s.on_id=p.stad_on_id left join (select distinct DN_ID from ai_alter_name_hist where ONTO_TYPE=#{pd.TERM_CATEGORY} and STATUS=0 ) y
 on p.ON_ID =y.dn_id where 1=1
 
	 and p.ON_ID!=p.STAD_ON_ID
 
 
	 and p.ON_ID = p.STAD_ON_ID
 
 
 	and P.SYNO_TYPE=#{pd.OSYN_TYPE}
 
 
 	and P.IS_DISABLE = #{pd.IS_DISABLED}
 
 
 	and P.UPDATE_MAN = #{pd.UPDATE_MAN}
 
 
 	and to_char(P.UPDATE_TIME,'YYYY-MM-DD') >= #{pd.UPDATE_TIME}
 
 
 	and to_char(P.UPDATE_TIME,'YYYY-MM-DD') <= #{pd.UPDATE_TIME_END}
 
 
 	and (P.ON_CHN like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%')
 		or P.ON_ENG like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%')
 		or s.on_chn like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%')
 	)
 
 order by P.ON_ID  

	order by P.UPDATE_TIME desc


	order by P.UPDATE_TIME asc

select n.dep_name standard_name,d.id,d.stad_id,d.dep_name,d.syno_type,d.is_disable,d.description,d.version,d.update_man,d.update_time,decode(Y.DN_ID,null,0,'',0,1) OSYN_EDIT_FLAG
 from on_dep_name d left join on_dep_name n on n.id=d.stad_id left join (select distinct DN_ID from ai_alter_name_hist where ONTO_TYPE=#{pd.TERM_CATEGORY} and STATUS=0 ) y
 on d.id =y.dn_id where 1=1
 
	 and d.id!=d.stad_id
 
 
	 and d.id = d.stad_id
 
 
 	and d.syno_type=#{pd.OSYN_TYPE}
 
 
 	and d.is_disable = #{pd.IS_DISABLED}
 
 
 	and d.update_man = #{pd.UPDATE_MAN}
 
 
 	and to_char(d.update_time,'YYYY-MM-DD') >= #{pd.UPDATE_TIME}
 
 
 	and to_char(d.update_time,'YYYY-MM-DD') <= #{pd.UPDATE_TIME_END}
 
 
 	and (d.dep_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%')
 		or n.dep_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%')
 	)
 
 order by d.id  

	order by d.update_time desc


	order by d.update_time asc

