left join on_operation_name b on (a.STAD_ON_ID=b.ON_ID and b.STAD_ON_ID=b.ON_ID 
	
		and b.STAD_ON_ID = #{pd.standardId} 
	
	) 
where 1=1 

	and	a.STAD_ON_ID != a.ON_ID 


	and	a.STAD_ON_ID = a.ON_ID 


	and a.STAD_ON_ID = #{pd.standardId} 

 
