select n.chn_name stadname, t.dn_id,t.stad_name_id,t.chn_name,t.eng_name,t.stad_chn_name,t.stad_eng_name, t.org_chn_name,t.org_eng_name,t.org_stnd_chn_name,t.org_stnd_eng_name,t.word_type,t.remark,t.is_disable, t.update_man,t.update_time,t.description,decode(Y.DN_ID,null,0,'',0,1) OSYN_EDIT_FLAG from on_drug_name t left join on_drug_name n on n.dn_id=t.stad_name_id left join (select distinct DN_ID from ai_alter_name_hist where ONTO_TYPE=#{pd.TERM_CATEGORY} and STATUS=0 ) y on t.dn_id =y.dn_id where
t.dn_id=t.stad_name_id
t.dn_id!=t.stad_name_id
and (t.chn_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.eng_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.stad_eng_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.org_chn_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.org_eng_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.org_stnd_chn_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.org_stnd_eng_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or n.chn_name like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') )
and to_char(t.UPDATE_TIME,'YYYY-MM-DD') >= #{pd.UPDATE_TIME}
and to_char(t.UPDATE_TIME,'YYYY-MM-DD') <= #{pd.UPDATE_TIME_END}
and t.word_type=#{pd.TERM_CATEGORY}
and t.is_disable=#{pd.IS_DISABLED}
order by t.dn_id
order by t.update_time desc
order by t.update_time asc

