SELECT t.dc_id,t.dn_id,t.dr_id,t.f_id,t.parent_id,t.dc_stad_chn,t.dc_stad_eng,t.dc_dos_stad_chn, t.dc_dos_stad_eng,t.dc_dr_stad_chn,t.dc_dr_stad_eng,t.org_dc,t.remark,t.is_disable,t.update_man,t.update_time, t.description,decode(Y.DN_ID,null,0,'',0,1) OSYN_EDIT_FLAG FROM ON_DRUG_CONCEPT t left join (select distinct DN_ID from ai_alter_name_hist where ONTO_TYPE=#{pd.TERM_CATEGORY} and STATUS=0 ) y on t.dc_id =y.dn_id where 1=1 and
and (t.dc_stad_chn like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.dc_stad_eng like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.dc_dos_stad_chn like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.dc_dos_stad_eng like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.dc_dr_stad_chn like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.dc_dr_stad_eng like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') or t.org_dc like CONCAT(CONCAT('%',#{pd.DN_NAME}),'%') )
and to_char(t.UPDATE_TIME,'YYYY-MM-DD') >= #{pd.UPDATE_TIME}
and to_char(t.UPDATE_TIME,'YYYY-MM-DD') <= #{pd.UPDATE_TIME_END}
and t.update_man = #{pd.UPDATE_MAN}
and t.is_disable=#{pd.IS_DISABLED}
order by t.dc_id
order by t.update_time desc
order by t.update_time asc

