mapper.select:
select org_name dept_name, org_code dept_code, nvl(c,0) c, nvl(haskj,0) haskj, nvl(100*haskj / c,0) rate from (select org_code, org_name, count(*) c, sum(haskj) haskj from presc t where order_type = 1
and t.ORDER_DATE >= #{pd.beginDate}
and t.ORDER_DATE < #{pd.endDate}
and t.org_name = #{pd.org_name}
group by org_code, org_name) order by rate desc

select org_name dept_name, org_code dept_code, nvl(c,0) c, nvl(haskj,0) haskj, nvl(100*haskj / c,0) rate from (select org_code, org_name, count(*) c, (sum(haskj) - sum(decode(haskj, 0, 0, EXTEDRUG))) haskj from presc t where order_type = 1
and t.ORDER_DATE >= #{pd.beginDate}
and t.ORDER_DATE < #{pd.endDate}
and t.org_name = #{pd.org_name}
group by org_code, org_name) order by rate desc

select org_code dept_code, org_name dept_name, nvl(c,0) c, nvl(haskj,0) haskj, nvl(100*haskj / c,0) rate from (select org_code, org_name, count(*) c, sum(decode(haskj, 0, 0, 1)) haskj from (select org_code, org_name, patient_id, order_date, sum(haskj) haskj, sum(decode(haskj, 0, 0, EXTEDRUG)) EXTEDRUG from presc t where order_type = 1
and t.ORDER_DATE >= #{pd.beginDate}
and t.ORDER_DATE < #{pd.endDate}
and t.org_name = #{pd.org_name}
group by org_code, org_name, patient_id, order_date ) group by org_code, org_name ) order by rate desc

select org_code dept_code, org_name dept_name, nvl(c,0) c, nvl(haskj,0) haskj, nvl(100*haskj / c ,0) rate from (select org_code, org_name, count(*) c, (sum(decode(haskj, 0, 0, 1)) - sum(decode(EXTEDRUG, 0, 0, 1))) haskj from (select org_code, org_name, patient_id, order_date, sum(haskj) haskj, sum(decode(haskj, 0, 0, EXTEDRUG)) EXTEDRUG from presc t where order_type = 1
and t.ORDER_DATE >= #{pd.beginDate}
and t.ORDER_DATE < #{pd.endDate}
and t.org_name = #{pd.org_name}
group by org_code, org_name, patient_id, order_date) group by org_code, org_name) order by rate desc

select org_name, org_code, doctor_name, nvl(c,0) c, nvl(haskj,0) haskj, nvl(100*haskj /c,0) rate from (select org_code, org_name, count(*) c, sum(haskj) haskj, doctor_name from presc t where t.order_type = 1
and t.ORDER_DATE >= #{pd.beginDate}
and t.ORDER_DATE < #{pd.endDate}
and t.org_name = #{pd.org_name}
group by org_code, doctor_name, org_name) order by rate desc

select doctor_name, nvl(sum(haskj),0) haskj, nvl(100 * sum(haskj) / count(patient_id),0) rate, org_code, org_name, count(distinct patient_id) c from (select org_code, patient_id, decode(sum(haskj), 0, 0, 1) haskj, doctor_name, org_name from presc t where order_type = 1
and t.ORDER_DATE >= #{pd.beginDate}
and t.ORDER_DATE < #{pd.endDate}
group by org_code, patient_id, doctor_name, org_name) group by org_code, doctor_name, org_name order by rate desc
