mapper.select:
select org_name dept_name, org_code dept_code, nvl(c,0) c, nvl(haskj,0) haskj, nvl(100*haskj / c,0) rate from (select org_code, org_name, count(*) c, nvl(sum(adminjmsy),0) haskj from presc t where order_type = 1
and t.ORDER_DATE >= #{beginDate}
and t.ORDER_DATE < #{endDate}
and t.org_name = #{org_name}
group by org_code, org_name) order by rate desc

select org_code dept_code, org_name dept_name, nvl(c,0) c, nvl(haskj,0) haskj, nvl(100*haskj / c ,0) rate from (select org_code, org_name, count(*) c, sum(decode(haskj, 0, 0, 1)) haskj from (select org_code, org_name, patient_id, order_date, nvl(sum(adminjmsy),0) haskj from presc t where order_type = 1
and t.ORDER_DATE >= #{beginDate}
and t.ORDER_DATE < #{endDate}
and t.org_name = #{org_name}
group by org_code, org_name, patient_id, order_date ) group by org_code, org_name ) order by rate desc

select org_name, org_code, doctor_name, nvl(c,0) c, nvl(haskj,0) haskj, nvl(100*haskj / c,0) rate from (select org_code, org_name, count(*) c, nvl(sum(adminantijmsy),0) haskj, doctor_name from presc t where t.order_type = 1
and t.ORDER_DATE >= #{beginDate}
and t.ORDER_DATE < #{endDate}
and t.org_name = #{org_name}
group by org_code, doctor_name, org_name) order by rate desc

select doctor_name, nvl(sum(haskj),0) haskj, nvl(100*sum(haskj) / count(patient_id),0) rate, org_code, org_name, nvl(count(distinct patient_id),0) c from (select org_code, patient_id, decode(sum(adminantijmsy), 0, 0, 1) haskj, doctor_name, org_name from presc t where order_type = 1
and t.ORDER_DATE >= #{beginDate}
and t.ORDER_DATE < #{endDate}
group by org_code, patient_id, doctor_name, org_name) group by org_code, doctor_name, org_name order by rate desc
