mapper.select:
		select  
			
		from 
			
		where 
			patient_id  = #{pd.patient_id} and visit_id = #{pd.visit_id}
			
		   
			and REPEAT_INDICATOR = #{pd.REPEAT_INDICATOR} 
		
		
			and order_class = #{pd.order_class}
		
		 
			and discharge_date_time < to_date(#{endDate} ,'yyyy-mm-dd') 
		

		Order by ORDER_NO,order_sub_no
