mapper.select:
		select a.*,m.RS_DRUG_TYPES
		from presc a
		left join ( select NGROUPNUM,listagg( RS_DRUG_TYPE ,'@;@') within GROUP (order by NGROUPNUM) RS_DRUG_TYPES 
	         from  (
	         		select NGROUPNUM,RS_DRUG_TYPE  from RS_DRUG_CHECKRSLT group by NGROUPNUM,RS_DRUG_TYPE 
           			) group by NGROUPNUM
	    ) m on a.NGROUPNUM=m.NGROUPNUM
		where 1=1 
		
		
			and
				(
					 a. DIAGNOSIS_NAMES LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
				)
		
		
			and
					 a. ORG_NAME LIKE CONCAT(CONCAT('%', #{pd.ORG_NAME}),'%')
		
		 
			and t.ISCHECKTRUE = #{pd.ISCHECKTRUE} 
		
		 
			and t.ISORDERCHECK = #{pd.ISORDERCHECK}
		
		order by ORDER_DATE desc

mapper.update:
update presc set NGROUPNUM = #{Ngroupnum} ,ISCHECKTRUE = #{ISCHECKTRUE}
	
		,ISORDERCHECK = #{ISORDERCHECK}
	
where id=#{id}
