mapper.select:
		select a.*,m.RS_DRUG_TYPES
		from presc_anti a  
	    
		    inner 
		
	     
		    left 
		
		 join ( select NGROUPNUM,listagg( RS_DRUG_TYPE ,'@;@') within GROUP (order by NGROUPNUM) RS_DRUG_TYPES 
	         from  (
	         		select NGROUPNUM,RS_DRUG_TYPE  
	         		from RS_DRUG_CHECKRSLT 
	         		where 

	         		in_rs_type = 5 
	         		 
			    		and RS_DRUG_TYPE = #{pd.RS_DRUG_TYPE}   
			    	
	         		group by NGROUPNUM,RS_DRUG_TYPE 
           	) group by NGROUPNUM
	    ) m on a.NGROUPNUM=m.NGROUPNUM
		where 1=1 
		
			and  a.id = #{pd.id}
		
		
		
			and
				(
					 a. DIAGNOSIS_NAMES LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
				)
		
		
			and a.ORG_NAME LIKE CONCAT(CONCAT('%', #{pd.ORG_NAME}),'%')
		
		 
			and a.DOCTOR_NAME = #{pd.DOCTOR_NAME}
		
		
		
			
				and ( a.ISCHECKTRUE =2	or a.ISCHECKTRUE  is null )
			
			
				  
					and a.ISCHECKTRUE  = #{pd.ISCHECKTRUE }
				
			
		
		 
			and a.ISORDERCHECK = 1
		
		 
			and (a.ISORDERCHECK = 0 or a.ISORDERCHECK is null ) 
		

			and a.ORDER_DATE >= #{pd.beginDate} 


			and a.ORDER_DATE < #{pd.endDate}

		 
			 and exists( select PRESC_ID from presc_detail q 
			 	where a.id = q.PRESC_ID 
				and q.DRUG_NAME = #{pd.DRUG_NAME}
			 )
		
		 
			and a.HASKJ = #{pd.HASKJ}
		
		 
			and a.HASZS = #{pd.HASZS}
		
		 
			and a.ELJSY = #{pd.ELJSY}
		
		 
			and a.DDRUG = #{pd.DDRUG}
		
		 
			and a.FSDRUG = #{pd.FSDRUG}
		
		 
			and a.YLJSY = #{pd.YLJSY}
		
		 
			and a.GZDRUG = #{pd.GZDRUG}
		
		 
			and a.MDRUG = #{pd.MDRUG}
		
		 
			and a.DMDRUG = #{pd.DMDRUG}
		
		 
			and a.EXPERT_ID is null
		
		 
			and a.EXPERT_ID is not null
		
		
			
				Order by DBMS_RANDOM.VALUE 
			
			
				Order by ORDER_DATE desc

		select a.*,m.RS_DRUG_TYPES from presc_anti a
		 left join ( select NGROUPNUM,listagg( RS_DRUG_TYPE ,'@;@') within GROUP (order by NGROUPNUM) RS_DRUG_TYPES 
	         from  (
	         		select NGROUPNUM,RS_DRUG_TYPE  
	         		from RS_DRUG_CHECKRSLT 
	         		where 

	         		in_rs_type = 5 
	         		group by NGROUPNUM,RS_DRUG_TYPE 
           	) group by NGROUPNUM
	    ) m on a.NGROUPNUM=m.NGROUPNUM
		
		where id = #{id}

select a.* from presc_anti  a 
where  a.id != #{ID}
and a.PATIENT_ID = #{PATIENT_ID}
and a.ORDER_DATE = #{ORDER_DATE}

select a.* 
from presc_detail a, presc_anti b 
where a.presc_id = b.id 
and b.id != #{ID}
and b.PATIENT_ID = #{PATIENT_ID}   
and b.ORDER_DATE = #{ORDER_DATE}

mapper.update:
update presc_anti set NGROUPNUM = #{Ngroupnum} ,ISCHECKTRUE = #{ISCHECKTRUE}
	
		,ISORDERCHECK = 1
	
where id=#{id}

mapper.select:
select count(id) ALL_COUNT,sum(DRUG_COUNT) DRUG_COUNT_SUM,sum(HASZS) HASZS_SUM
	,sum(BASEDRUG_COUNT) BASEDRUG_COUNT_SUM,sum(AMOUNT) AMOUNT_SUM,sum(HASKJ) HASKJ_SUM
	,round(avg(AMOUNT),2) AMOUNT_AVG,round(avg(DRUG_COUNT),2) DRUG_COUNT_AVG
	, decode(count(id),0,0.00,round(100*sum(HASZS)/count(id),2)) HASZS_PERSENTS
	,decode(count(id),0,0.00,round(100*sum(HASKJ)/count(id),2)) HASKJ_PERSENTS
	,decode(count(id),0,0.00,round(100*sum(BASEDRUG_COUNT)/count(id),2)) BASEDRUG_COUNT_PERSENTS 
from presc_anti a
where 1=1


	and
		(
			 a. DIAGNOSIS_NAMES LIKE CONCAT(CONCAT('%', #{keywords}),'%')
		)


	and a.ORG_NAME LIKE CONCAT(CONCAT('%', #{ORG_NAME}),'%')

 
	and a.DOCTOR_NAME = #{DOCTOR_NAME}

 
	and a.ISCHECKTRUE = #{ISCHECKTRUE} 

 
	and a.ISORDERCHECK = #{ISORDERCHECK}

   
	and a.ORDER_DATE >= #{beginDate} 

 
	and a.ORDER_DATE < #{endDate}

 
	 and exists( select PRESC_ID from presc_detail q 
	 	where a.id = q.PRESC_ID 
		and q.DRUG_NAME = #{DRUG_NAME}
	 )

 
	and a.HASKJ = #{HASKJ}

 
	and a.HASZS = #{HASZS}

 
	and a.ELJSY = #{ELJSY}

 
	and a.DDRUG = #{DDRUG}

 
	and a.FSDRUG = #{FSDRUG}

 
	and a.YLJSY = #{YLJSY}

 
	and a.GZDRUG = #{GZDRUG}

 
	and a.MDRUG = #{MDRUG}

 
	and a.DMDRUG = #{DMDRUG}

select a.rs_id, a.drug_id1, (select p.drug_name || '[' || p.drug_spec || '][' || p.dosage || p.dosage_units || ',' || p.frequency || ',' || p.administration || '][' || p.amount || p.units || ']' from patienthistory.presc_detail p where p.presc_id = b.id and p.drug_code = a.drug_id1 and p.order_no = a.rec_main_no1 and p.order_sub_no = a.rec_sub_no1) drugdetail1, a.drug_id1_name, a.rec_main_no1, a.rec_sub_no1, a.drug_id2, a.drug_id2_name, (select p.drug_name || '[' || p.drug_spec || '][' || p.dosage || p.dosage_units || ',' || p.frequency || ',' || p.administration || '][' || p.amount || p.units || ']' from patienthistory.presc_detail p where p.presc_id = c.id and p.drug_code = a.drug_id2 and p.order_no = a.rec_main_no2 and p.order_sub_no = a.rec_sub_no2) drugdetail2, a.rec_main_no2, a.rec_sub_no2, a.ngroupnum, a.rs_drug_type, a.in_rs_type, a.alert_level, a.alert_hint, a.alert_cause, a.checkdate, a.CHECKPEOPLE, a.RELATION_ID1, a.RELATION_ID2, b.DOCTOR_NAME DOCTOR_NAME1, b.ORG_NAME ORG_NAME1, b.PRESC_NO PRESC_NO1, c.DOCTOR_NAME DOCTOR_NAME2, c.ORG_NAME ORG_NAME2, c.PRESC_NO PRESC_NO2 from RS_DRUG_CHECKRSLT a left join presc_anti b on b.id = a.RELATION_ID1 left join presc_anti c on c.id = a.RELATION_ID2 where a.ngroupnum = #{ngroupnum}
and CHECKPEOPLE is not null
order by a.rs_drug_type ,a.drug_id1 ,a.drug_id2
