mapper.select:
select drug_code, drug_name, package_spec, units, is_anti, firm_id, sum(amount) amounts, sum(cost) costs from dr_drugdaycost t where 1=1
and dr_date >= to_date(#{pd.beginDate} ,'yyyy-mm-dd')
and dr_date <= to_date(#{pd.endDate} ,'yyyy-mm-dd')
and drug_name = #{pd.drug_name}
and DEPT_NAME = #{pd.DEPT_NAME}
and DISPENSARY_NAME = #{pd.DISPENSARY_NAME}
and TOXI_PROPERTY = #{pd.TOXI_PROPERTY}
group by drug_code,drug_name, package_spec,firm_id,is_anti,units
order by sum(cost) desc
order by drug_code
order by firm_id
order by sum(cost) asc
order by sum(cost) desc

select doctor_code, doctor_name, drug_code, drug_name, package_spec, firm_id, is_anti, units, toxi_property, sum(cost) costs, sum(amount) amounts from dr_drugdoctordeptcost dg where 1=1
and dr_date >= to_date(#{pd.beginDate} ,'yyyy-mm-dd')
and dr_date <= to_date(#{pd.endDate} ,'yyyy-mm-dd')
and drug_name = #{pd.drug_name}
and DEPT_NAME = #{pd.DEPT_NAME}
and DOCTOR_NAME = #{pd.DOCTOR_NAME}
and DISPENSARY_NAME = #{pd.DISPENSARY_NAME}
and TOXI_PROPERTY = #{pd.TOXI_PROPERTY}
group by doctor_code,doctor_name, drug_code,drug_name, package_spec,firm_id, is_anti,units,toxi_property
order by sum(cost) desc
order by drug_code
order by firm_id
order by sum(cost) asc
order by sum(cost) desc
order by dept_code

select patient_id, identity, dr_date, drug_code, DRUG_NAME, doctor_code, dept_code, dept_name, pat_name, doctor_name, units, package_spec, firm_id, is_anti, toxi_property, charge_type, sum(amount) amounts, sum(cost) costs from DR_DrugPatDDCost dp where 1 = 1
and dr_date >= to_date(#{pd.beginDate} ,'yyyy-mm-dd')
and dr_date <= to_date(#{pd.endDate} ,'yyyy-mm-dd')
and PATIENT_ID = #{pd.PATIENT_ID}
and CHARGE_TYPE = #{pd.CHARGE_TYPE}
and IDENTITY = #{pd.IDENTITY}
and drug_name = #{pd.drug_name}
and DEPT_NAME = #{pd.DEPT_NAME}
and DOCTOR_NAME = #{pd.DOCTOR_NAME}
and DISPENSARY_NAME = #{pd.DISPENSARY_NAME}
and TOXI_PROPERTY = #{pd.TOXI_PROPERTY}
group by patient_id, identity, dr_date, drug_code,DRUG_NAME, doctor_code, dept_code, dept_name, pat_name, doctor_name, units, package_spec, firm_id, is_anti, toxi_property, charge_type, drug_name
order by sum(cost) desc
order by drug_code
order by firm_id
order by sum(cost) asc
order by sum(cost) desc
order by dept_code

select dr_date, drug_code, drug_name, toxi_property, package_spec, firm_id, units, sum(cost) costs, sum(amount) amounts from dr_drugdoctordeptcost dg where 1=1
and dr_date >= to_date(#{pd.beginDate} ,'yyyy-mm-dd')
and dr_date <= to_date(#{pd.endDate} ,'yyyy-mm-dd')
and drug_name = #{pd.drug_name}
and DEPT_NAME = #{pd.DEPT_NAME}
and DOCTOR_NAME = #{pd.DOCTOR_NAME}
and DISPENSARY_NAME = #{pd.DISPENSARY_NAME}
and TOXI_PROPERTY = #{pd.TOXI_PROPERTY}
group by dr_date, drug_code, drug_name, toxi_property, package_spec, firm_id, units
order by dr_date
order by drug_code,dr_date
order by firm_id,dr_date
order by sum(cost) asc,dr_date
order by sum(cost) desc,dr_date
order by dept_code ,dr_date
order by dr_date,drug_code,dept_code
